parameters.functionScript:
const server = Inputs.Server || '';
const groupId = Inputs.GroupID || '';
const hostIdArray = Inputs.Hosts || [];

if (!server || !groupId || !hostIdArray.length) throw new Error('Invalid inputs');

// Use for...of loop to handle async operations
for (const item of hostIdArray) {
  const hostId = item.HostID || '';
  if (!hostId) continue;

  const url = `${server}/api/groups/${groupId}/add/${hostId}`;

  try {
    const response = await fetch(url, {
      method: 'POST',
    });

    if (!response.ok) throw new Error('Failed to add host');

    Outputs.Success();
  } catch (error) {
    Outputs.error = error;
    Outputs.Failure();
  }
}
